import React from 'react'

import './picture-card.css'

const cont = {
    backgroundColor: "#eee",
    overflow: "hidden",
    position: "relative"
};

const PictureCard = ({ photo, margin }) => {
    return (
        <div class="image-container">
            <div class="image">
                <img id="hover-this" src={photo.src} alt=""
                    height="auto"
                    width={"calc((100% - 30px) / 2.63153)"}
                    boxSizing="content-box"
                    aspectRatio={photo.width + '/' + photo.height}
                    sizes="(max-width: 767px) calc((100vw - 32px - 30px) / 2.63153), (max-width: 1279px) calc((100vw - 288px - 30px) / 2.63153), calc((992px - 30px) / 2.63153)"
                />
                <div class="bottom-right overlay-text" id="show-this">{photo.caption}</div>
            </div>

        </div>
    );
};

export default PictureCard
